<?php    session_start();    ob_start();    require_once('../inc/functions.php');    require_once('../inc/mysqli_connect.php');?><!DOCTYPE html><html><head>	<meta charset='UTF-8' />    <link href="../css/login.css" type="text/css" rel="stylesheet" />    <script type="text/javascript" src="http://ajax.googleapis.com/ajax/libs/jquery/1.8.3/jquery.min.js"></script>    <script>     $(document).ready(function() {            $('form').hide();            $('a').click(function() {               $(this).parent('div').prev('form').slideToggle('normal');               $(this).toggleClass('active');               return false;            });        });     </script>	<title>Đăng nhập trang quản trị | Elextronic</title></head><body><?php    if ($_SERVER['REQUEST_METHOD'] == 'POST') {        //Mặc định cho các trường nhập liệu là FALSE            $e = $p = FALSE;        //Validate email        if (isset($_POST['email']) && filter_var($_POST['email'], FILTER_VALIDATE_EMAIL)) {            $e = mysqli_real_escape_string($dbc, $_POST['email']);        }        //Validate pasword        if (isset($_POST['pass']) && preg_match('/^[\w\'.-]{4,20}$/', $_POST['pass'])) {            $p = mysqli_real_escape_string($dbc, $_POST['pass']);        }        if ($e && $p) {            //Nếu các biến đều có giá trị, truy vấn CSDL            //Bắt đầu truy vấn CSDL để lấy thông tin người dùng            $q = "SELECT user_id, last_name, user_level FROM users WHERE (email = '{$e}' AND pass = SHA1('$p') AND user_level = 2) AND active IS NULL LIMIT 1";            $r = mysqli_query($dbc, $q);                confirm_query($r, $q);            if (mysqli_num_rows($r) == 1) {                //Nếu tìm thấy thông tin người dùng trong CSDL, sẽ chuyển hướng người dùng về trang thích hợp                list($uid, $ln, $user_level) = mysqli_fetch_array($r, MYSQLI_NUM);                $_SESSION['uid'] = $uid;                $_SESSION['last_name'] = $ln;                $_SESSION['user_level'] = $user_level;                redirect_to('admin/index.php');            } else {                redirect_to('admin/login.php');            }        } else {            redirect_to('admin/login.php');        }    } //end if submit form?>    <div id="wrapper">        <form action="" method="post">            <p>                <label for="email">Email:</label>                <input type="text" name="email" size="30" />            </p>            <p>                <label for="pass">Mật khẩu:</label>                <input type="password" name="pass" size="30" />            </p>            <p>                <input type="submit" name="submit" value="Login" />            </p>        </form>        <div class='login'>            <a href="#">Đăng nhập</a>        </div>    </div></body></html>